example.code:
import processing.video.*;
Capture myCapture;

void setup() 
{
  size(200, 200);
  String s = "Logitech QuickCam Messenger-WDM";
  myCapture = new Capture(this, s, width, height, 30);
}

void draw() {
  if(myCapture.available()) {
    myCapture.read();
  }
  image(myCapture, 0, 0);
}

void mousePressed() {
  myCapture.stop();
}
