example.code:
import processing.video.*;
Movie myMovie;

void setup() {
  size(200, 200);
  framerate(30);
  myMovie = new Movie(this, "totoro.mov");
  // Prints the duration of the movie
  println(myMovie.duration());
  myMovie.play();
}

void draw() {
  if(myMovie.available()) {
    myMovie.read();
  }
  image(myMovie, 0, 0);
}
