example.code:
float yPos = 0.0; 

void setup() { 
  size(200, 200); 
  frameRate(30);
} 

void draw() { 
  background(204);
  yPos = yPos - 1.0;
  if(yPos < 0) {
    yPos = height;
  }
  line(0, yPos, width, yPos);
}

void setup() {
  size(200, 200);
}

void draw() { }

void mousePressed() {
  line(mouseX, 10, mouseX, 90);
}
