example.code:
PImage b;
b = loadImage("topanga.jpg");
image(b, 0, 0);
filter(THRESHOLD);

PImage b;
b = loadImage("topanga.jpg");
image(b, 0, 0);
filter(GRAY);

PImage b;
b = loadImage("topanga.jpg");
image(b, 0, 0);
filter(INVERT);
save("filter_invert.tif");

PImage b;
b = loadImage("topanga.jpg");
image(b, 0, 0);
filter(GRAY);
filter(POSTERIZE, 4);

PImage b;
b = loadImage("topanga.jpg");
image(b, 0, 0);
filter(BLUR, 6);
