example.code:
BImage img = loadImage("rockies.jpg");
BImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 100, 100, ADD); 
image(img, 0, 0);
image(img2, 0, 0);

BImage img = loadImage("rockies.jpg");
BImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 100, 100, SUBSTRACT); 
image(img, 0, 0);
image(img2, 0, 0);

BImage img = loadImage("rockies.jpg");
BImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 100, 100, DARKEST); 
image(img, 0, 0);
image(img2, 0, 0);

BImage img = loadImage("rockies.jpg");
BImage img2 = loadImage("degaul.jpg"); 
img.blend(img2, 0, 0, 33, 100, 67, 0, 100, 100, LIGHTEST); 
image(img, 0, 0);
image(img2, 0, 0);

root.syntax:
img.blend(sx, sy, dx, dy, mode);
img.blend(srcImg, sx, sy, dx, dy, mode);
img.blend(sx1, sy1, sx2, sy2, dx1, dy1, dx2, dy2, mode);
img.blend(srcImg, sx1, sy1, sx2, sy2, dx1, dy1, dx2, dy2, mode);
