example.code:
for(int i = 5; i &lt; 95; i += 5) {
  if(i &lt; 35) {
    line( 30, i, 80, i );
  } else {
    line( 20, i, 90, i );
  }
}

for(int i = 5; i &lt; 95; i += 5) {
  if(i &lt; 35) {
    line( 30, i, 80, i );
  } else if (i &lt; 65) {
    line( 20, i, 90, i );
  } else {
    line( 0, i, 100, i ); 
  }
}

