example.code:
int val = 30;

void draw() {
  int t = timestwo(val);
  println(t);
}

int timestwo(int dVal) {
  dVal = dVal * 2;
  return dVal;
}

int[] vals = {10, 20, 30}; 

void draw() { 
  int[] t = timestwo(vals); 
  println(t); 
  noLoop();
} 

int[] timestwo(int[] dVals) { 
  for(int i = 0; i &lt; dVals.length; i++) { 
    dVals[i] = dVals[i] * 2; 
  } 
  return dVals; 
}

void draw() {
  background(204);
  line(0, 0, width, height);
  if(mousePressed) {
    return;
  }
  line(0, height, width, 0);
}
